const chalk = require('chalk');

const NO_LOGS = -1;
const STATUS_LOGS = 0;
const INFO_LOGS = 1;
const DEBUG_LOGS = 2;

var logLevel = INFO_LOGS;
exports.setLogLevel = (level) => {
    switch (level) {
        case "none":
            logLevel = NO_LOGS;
            break;
        case "info":
            logLevel = INFO_LOGS;
            break;
        case "debug":
            logLevel = DEBUG_LOGS;
            break;
        case "status":
        default:
            logLevel = STATUS_LOGS;
            break;
    }
}

exports.logDebug = (message) => {
    if (logLevel < DEBUG_LOGS) {
        return;
    }
    console.log(chalk.white(message));
}

exports.logInfo = (message) => {
    if (logLevel < INFO_LOGS) {
        return;
    }
    console.log(chalk.yellow(message));
}

exports.logError = (message) => {
    if (logLevel < STATUS_LOGS) {
        return;
    }
    console.log(chalk.redBright(message));
}

exports.logSuccess = (message) => {
    if (logLevel < STATUS_LOGS) {
        return;
    }
    console.log(chalk.greenBright(message));
}